import { CGFinterface, CGFapplication, dat } from '../lib/CGF.js';
import { MaterialUpdater } from './rendering/MaterialUpdater.js';

/**
* MyInterface class, creating a GUI interface.
*/
export class MyInterface extends CGFinterface {
    /**
     * @constructor
     */
    constructor() {
        super();
    }

    /**
     * Initializes the interface.
     * @param {CGFapplication} application
     */
    init(application) {
        super.init(application);
        // init GUI. For more information on the methods, check:
        //  http://workshop.chromeexperiments.com/examples/gui

        this.gui = new dat.GUI();

        // add a group of controls (and open/expand by defult)

        this.initKeys();

        return true;
    }

    onGraphLoaded() {
        this.materialUpdater = new MaterialUpdater(this.scene.sceneData);
        this.gui.add(this.scene, 'camerasIds', this.scene.camerasIds)
            .name('Active Camera')
            .onChange((value) => this.scene.setCamera(value));
        let folder = this.gui.addFolder('Lights');
        for(let i = 0; i < this.scene.lights.length; ++i) {
            folder.add(this.scene.lights[i], 'enabled')
                .name(this.scene.lightsIds[i])
                .onChange((value) => {
                    this.scene.setLight(i, value);
            });
        }
    }

    /**
     * initKeys
     */
    initKeys() {
        this.scene.gui=this;
        this.processKeyboard=function(){};
        this.activeKeys={};
    }

    processKeyDown(event) {
        this.activeKeys[event.code]=true;

        if(event.code == "KeyM") {
            this.materialUpdater.update();
        }
    };

    processKeyUp(event) {
        this.activeKeys[event.code]=false;
    };

    isKeyPressed(keyCode) {
        return this.activeKeys[keyCode] || false;
    }
}

import { CGFXMLreader } from '../lib/CGF.js';
import {SceneData} from "./models/SceneData.js";
import {ComponentsLinker} from "./parser/component/ComponentsLinker.js";
import {ParserErrorPrinter} from "./parser/ParserErrorPrinter.js";
import {GenericChildParser} from "./parser/GenericChildParser.js";
import {TransformationParser} from "./parser/TransformationParser.js";
import {MaterialParser} from "./parser/MaterialParser.js";
import {ComponentParser} from "./parser/component/ComponentParser.js";
import {PrimitiveParser} from "./parser/PrimitiveParser.js";
import {TextureParser} from "./parser/TextureParser.js";
import {ViewParser} from "./parser/ViewParser.js";
import { LightsParser } from './parser/LightsParser.js';
import {AmbientParser} from "./parser/AmbientParser.js";
import { MyView } from './models/wrappers/MyView.js';


// Order of the groups in the XML document.
const SCENE_INDEX = 0;
const VIEWS_INDEX = 1;
const AMBIENT_INDEX = 2;
const LIGHTS_INDEX = 3;
const TEXTURES_INDEX = 4;
const MATERIALS_INDEX = 5;
const TRANSFORMATIONS_INDEX = 6;
const PRIMITIVES_INDEX = 7;
const COMPONENTS_INDEX = 8;

/**
 * MySceneGraph class, representing the scene graph.
 */
export class MySceneGraph {
    /**
     * @constructor
     */
    constructor(filename, scene) {
        this.loadedOk = null;

        // Establish bidirectional references between scene and graph.
        this.scene = scene;
        this.sceneData = new SceneData(scene);
        scene.sceneData = this.sceneData;

        this.axisCoords = [];
        this.axisCoords['x'] = [1, 0, 0];
        this.axisCoords['y'] = [0, 1, 0];
        this.axisCoords['z'] = [0, 0, 1];

        // File reading 
        this.reader = new CGFXMLreader();

        /*
         * Read the contents of the xml file, and refer to this class for loading and error handlers.
         * After the file is read, the reader calls onXMLReady on this object.
         * If any error occurs, the reader calls onXMLError on this object, with an error message
         */
        this.reader.open('scenes/' + filename, this);
    }

    /*
     * Callback to be executed after successful reading
     */
    onXMLReady() {
        this.log("XML Loading finished.");
        const rootElement = this.reader.xmlDoc.documentElement;

        // Here should go the calls for different functions to parse the various blocks
        const error = this.parseXMLFile(rootElement);

        if (error != null) {
            this.onXMLError(error);
            return;
        }

        this.loadedOk = true;

        // As the graph loaded ok, signal the scene so that any additional initialization depending on the graph can take place
        this.scene.onGraphLoaded();
    }

    /**
     * Parses the XML file, processing each block.
     * @param {XML root element} rootElement
     */
    parseXMLFile(rootElement) {
        if (rootElement.nodeName != "sxs")
            return "root tag <sxs> missing";

        const nodes = rootElement.children;

        // Reads the names of the nodes to an auxiliary buffer.
        let nodeNames = [];

        for (let i = 0; i < nodes.length; i++) {
            nodeNames.push(nodes[i].nodeName);
        }

        let error;

        // Processes each node, verifying errors.
        // <scene>
        let index;
        if ((index = nodeNames.indexOf("scene")) == -1)
            return "tag <scene> missing";
        else {
            if (index != SCENE_INDEX)
                this.onXMLMinorError("tag <scene> out of order " + index);

            //Parse scene block
            if ((error = this.parseScene(nodes[index])) != null)
                return error;
        }

        // <views>
        if ((index = nodeNames.indexOf("views")) == -1)
            return "tag <views> missing";
        else {
            if (index != VIEWS_INDEX)
                this.onXMLMinorError("tag <views> out of order");

            //Parse views block
            if ((error = this.parseView(nodes[index])) != null)
                return error;
        }

        // <ambient>
        if ((index = nodeNames.indexOf("ambient")) == -1)
            return "tag <ambient> missing";
        else {
            if (index != AMBIENT_INDEX)
                this.onXMLMinorError("tag <ambient> out of order");

            //Parse ambient block
            if ((error = this.parseAmbient(nodes[index])) != null)
                return error;
        }

        // <lights>
        if ((index = nodeNames.indexOf("lights")) == -1)
            return "tag <lights> missing";
        else {
            if (index != LIGHTS_INDEX)
                this.onXMLMinorError("tag <lights> out of order");

            //Parse lights block
            if ((error = this.parseLights(nodes[index])) != null)
                return error;
        }
        // <textures>
        if ((index = nodeNames.indexOf("textures")) == -1)
            return "tag <textures> missing";
        else {
            if (index != TEXTURES_INDEX)
                this.onXMLMinorError("tag <textures> out of order");

            //Parse textures block
            if ((error = this.parseTextures(nodes[index])) != null)
                return error;
        }

        // <materials>
        if ((index = nodeNames.indexOf("materials")) == -1)
            return "tag <materials> missing";
        else {
            if (index != MATERIALS_INDEX)
                this.onXMLMinorError("tag <materials> out of order");

            //Parse materials block
            if ((error = this.parseMaterials(nodes[index])) != null)
                return error;
        }

        // <transformations>
        if ((index = nodeNames.indexOf("transformations")) == -1)
            return "tag <transformations> missing";
        else {
            if (index != TRANSFORMATIONS_INDEX)
                this.onXMLMinorError("tag <transformations> out of order");

            //Parse transformations block
            if ((error = this.parseTransformations(nodes[index])) != null)
                return error;
        }

        // <primitives>
        if ((index = nodeNames.indexOf("primitives")) == -1)
            return "tag <primitives> missing";
        else {
            if (index != PRIMITIVES_INDEX)
                this.onXMLMinorError("tag <primitives> out of order");

            //Parse primitives block
            if ((error = this.parsePrimitives(nodes[index])) != null)
                return error;
        }

        // <components>
        if ((index = nodeNames.indexOf("components")) == -1)
            return "tag <components> missing";
        else {
            if (index != COMPONENTS_INDEX)
                this.onXMLMinorError("tag <components> out of order");

            //Parse components block
            if ((error = this.parseComponents(nodes[index])) != null)
                return error;
        }
        this.log("all parsed");
    }

    /**
     * Parses the <scene> block. 
     * @param {scene block element} sceneNode
     */
    parseScene(sceneNode) {

        // Get root of the scene.
        let root = this.reader.getString(sceneNode, 'root')
        if (root == null)
            return "no root defined for scene";

        this.sceneData.root = root;

        // Get axis length        
        let axis_length = this.reader.getFloat(sceneNode, 'axis_length');
        if (axis_length == null)
            this.onXMLMinorError("no axis_length defined for scene; assuming 'length = 1'");

        this.sceneData.referenceLength = axis_length || 1;

        return null;
    }

    /**
     * Parses the <views> block.
     * @param {view block element} viewsNode
     */
    parseView(viewsNode) {
        let result = GenericChildParser.parse(viewsNode, this.reader, this.scene, ViewParser, "view");
        ParserErrorPrinter.print(result.getErrors());
        console.log("Views", result);
        this.sceneData.views = result.getValue();

        let defaultView = this.reader.getString(viewsNode, 'default');
        this.sceneData.defaultView = this.reader.getString(viewsNode, 'default');

        if(defaultView == null || !(defaultView in Object.keys(this.sceneData.views)) ) {
            if(Object.keys(this.sceneData.views).length > 0) {
                defaultView = Object.keys(this.sceneData.views)[0];
                this.onXMLMinorError("no default view defined, defaulting to view with id=" + defaultView);
            } else {
                defaultView = 'default_view';
                this.sceneData.views['default_view'] = MyView.instantiate('default_view', 1.5, 0.1, 500, vec3.fromValues(15, 15, 15), vec3.fromValues(0, 0, 0));
                this.onXMLMinorError("no default view defined and no views were defined, creating a default view");
            }
        }

        this.sceneData.defaultView = defaultView;

        return null;
    }

    /**
     * Parses the <ambient> node.
     * @param {ambient block element} ambientsNode
     */
    parseAmbient(ambientsNode) {
        let result = AmbientParser.parse(ambientsNode, this.reader);
        ParserErrorPrinter.print(result.getErrors());
        console.log("Ambient", result);
        this.sceneData.ambient = result.getValue()['ambient'];
        this.sceneData.background = result.getValue()['background'];
        return null;
    }

    /**
     * Parses the <light> node.
     * @param {lights block element} lightsNode
     */
    parseLights(lightsNode) {
        let result = LightsParser.parse(lightsNode, this.reader);
        this.sceneData.lights = result.getValue();
        ParserErrorPrinter.print(result.getErrors());
        console.log("Lights", result.getValue());
        return null;
    }

    /**
     * Parses the <textures> block. 
     * @param {textures block element} texturesNode
     */
    parseTextures(texturesNode) {
        let result = GenericChildParser.parse(texturesNode, this.reader, this.scene, TextureParser, "texture");
        this.sceneData.textures = result.getValue();
        console.log("Textures", result);
        return null;
    }

    /**
     * Parses the <materials> node.
     * @param {materials block element} materialsNode
     */
    parseMaterials(materialsNode) {
        let result = GenericChildParser.parse(materialsNode, this.reader, this.scene, MaterialParser, "material");
        this.sceneData.materials = result.getValue();
        console.log("Materials", result);
        return null;
    }

    /**
     * Parses the <transformations> block.
     * @param {transformations block element} transformationsNode
     */
    parseTransformations(transformationsNode) {
        let result = GenericChildParser.parse(transformationsNode, this.reader, this.scene, TransformationParser, "transformation");
        this.sceneData.transformations = result.getValue();
        ParserErrorPrinter.print(result.getErrors());
        console.log("Transformations", result);
        return null;
    }

    /**
     * Parses the <primitives> block.
     * @param {primitives block element} primitivesNode
     */
    parsePrimitives(primitivesNode) {
        let result = GenericChildParser.parse(primitivesNode, this.reader, this.scene, PrimitiveParser, "primitive");
        this.sceneData.primitives = result.getValue();
        console.log("Primitives", result);
        return null;
    }

    /**
   * Parses the <components> block.
   * @param {components block element} componentsNode
   */
    parseComponents(componentsNode) {
        let result = GenericChildParser.parse(componentsNode, this.reader, this.sceneData, ComponentParser, "component");
        ParserErrorPrinter.print(result.getErrors());
        this.sceneData.components = result.getValue();

        console.log("Linking child components");
        ParserErrorPrinter.print(ComponentsLinker.link(this.sceneData));

        console.log("Components", result);
        return null;
    }

    /**
     * Parse the color components from a node
     * @param {block element} node
     * @param {message to be displayed in case of error} messageError
     */
    parseColor(node, messageError) {
        var color = [];

        // R
        var r = this.reader.getFloat(node, 'r');
        if (!(r != null && !isNaN(r) && r >= 0 && r <= 1))
            return "unable to parse R component of the " + messageError;

        // G
        var g = this.reader.getFloat(node, 'g');
        if (!(g != null && !isNaN(g) && g >= 0 && g <= 1))
            return "unable to parse G component of the " + messageError;

        // B
        var b = this.reader.getFloat(node, 'b');
        if (!(b != null && !isNaN(b) && b >= 0 && b <= 1))
            return "unable to parse B component of the " + messageError;

        // A
        var a = this.reader.getFloat(node, 'a');
        if (!(a != null && !isNaN(a) && a >= 0 && a <= 1))
            return "unable to parse A component of the " + messageError;

        color.push(...[r, g, b, a]);

        return color;
    }

    /*
     * Callback to be executed on any read error, showing an error on the console.
     * @param {string} message
     */
    onXMLError(message) {
        console.error("XML Loading Error: " + message);
        this.loadedOk = false;
    }

    /**
     * Callback to be executed on any minor error, showing a warning on the console.
     * @param {string} message
     */
    onXMLMinorError(message) {
        console.warn("Warning: " + message);
    }

    /**
     * Callback to be executed on any message.
     * @param {string} message
     */
    log(message) {
        console.log("   " + message);
    }
}

import { CGFscene } from '../lib/CGF.js';
import { CGFaxis,CGFcamera } from '../lib/CGF.js';
import {SceneRenderer} from "./rendering/SceneRenderer.js";


var DEGREE_TO_RAD = Math.PI / 180;

/**
 * XMLscene class, representing the scene that is to be rendered.
 */
export class XMLscene extends CGFscene {
    /**
     * @constructor
     * @param {MyInterface} myinterface 
     */
    constructor(myinterface) {
        super();

        this.interface = myinterface;
    }

    /**
     * Initializes the scene, setting some WebGL defaults, initializing the camera and the axis.
     * @param {CGFApplication} application
     */
    init(application) {
        super.init(application);

        this.sceneInited = false;

        this.initDefaultCameras();

        this.enableTextures(true);

        this.gl.clearDepth(100.0);
        this.gl.enable(this.gl.DEPTH_TEST);
        this.gl.enable(this.gl.CULL_FACE);
        this.gl.depthFunc(this.gl.LEQUAL);

        this.axis = new CGFaxis(this);
        this.setUpdatePeriod(100);
    }

    /**
     * Initializes the scene cameras.
     */
    initDefaultCameras() {
        this.camera = new CGFcamera(0.4, 0.1, 500, vec3.fromValues(15, 15, 15), vec3.fromValues(0, 0, 0));
    }

    // todo:
    initCameras() {
        this.setCamera(this.sceneData.defaultView);
        this.camerasIds = Object.keys(this.sceneData.views);
    }

    setCamera(cameraId) {
        this.camera = this.sceneData.views[cameraId].getCGFCamera();
        this.interface.setActiveCamera(this.camera);
    }

    setLight(lightIndex, enabled) {
        if(enabled) {
            this.lights[lightIndex].enable();
        } else {
            this.lights[lightIndex].disable();
        }
        this.lights[lightIndex].update();
    }


    /**
     * Initializes the scene lights with the values read from the XML file.
     */
    initLights() {
        var i = 0;
        // Lights index.

        // Reads the lights from the scene graph.
        this.lightsIds = [];
        for (var key in this.sceneData.lights) {
            if (i >= 8)
                break;              // Only eight lights allowed by WebGL.

            if (this.sceneData.lights.hasOwnProperty(key)) {
                this.lightsIds.push(key);
                var light = this.sceneData.lights[key];

                this.lights[i].setPosition(light[2][0], light[2][1], light[2][2], light[2][3]);
                this.lights[i].setAmbient(light[3][0], light[3][1], light[3][2], light[3][3]);
                this.lights[i].setDiffuse(light[4][0], light[4][1], light[4][2], light[4][3]);
                this.lights[i].setSpecular(light[5][0], light[5][1], light[5][2], light[5][3]);

                if (light[1] == "spot") {
                    this.lights[i].setSpotCutOff(light[6]);
                    this.lights[i].setSpotExponent(light[7]);
                    this.lights[i].setSpotDirection(light[8][0], light[8][1], light[8][2]);
                }

                this.lights[i].setVisible(true);
                if (light[0])
                    this.lights[i].enable();
                else
                    this.lights[i].disable();

                this.lights[i].update();

                i++;
            }
        }
        this.lights = this.lights.slice(0, i);
    }

    setDefaultAppearance() {
        this.setAmbient(0.2, 0.4, 0.8, 1.0);
        this.setDiffuse(0.2, 0.4, 0.8, 1.0);
        this.setSpecular(0.2, 0.4, 0.8, 1.0);
        this.setShininess(10.0);
    }
    /** Handler called when the graph is finally loaded. 
     * As loading is asynchronous, this may be called already after the application has started the run loop
     */
    onGraphLoaded() {
        this.axis = new CGFaxis(this, this.sceneData.referenceLength);

        this.gl.clearColor(this.sceneData.background[0], this.sceneData.background[1], this.sceneData.background[2], this.sceneData.background[3]);

        this.setGlobalAmbientLight(this.sceneData.ambient[0], this.sceneData.ambient[1], this.sceneData.ambient[2], this.sceneData.ambient[3]);

        this.initLights();
        this.initCameras();

        this.renderer = new SceneRenderer(this.sceneData);

        this.interface.onGraphLoaded();

        this.sceneInited = true;
    }

    /**
     * Displays the scene.
     */
    display() {
        // ---- BEGIN Background, camera and axis setup

        // Clear image and depth buffer everytime we update the scene
        this.gl.viewport(0, 0, this.gl.canvas.width, this.gl.canvas.height);
        this.gl.clear(this.gl.COLOR_BUFFER_BIT | this.gl.DEPTH_BUFFER_BIT);

        // Initialize Model-View matrix as identity (no transformation
        this.updateProjectionMatrix();
        this.loadIdentity();

        // Apply transformations corresponding to the camera position relative to the origin
        this.applyViewMatrix();

        this.pushMatrix();
        this.axis.display();

        for (var i = 0; i < this.lights.length; i++) {
            this.lights[i].setVisible(true);
            this.lights[i].update();
        }

        if (this.sceneInited) {
            // Draw axis
            this.setDefaultAppearance();

            // Displays the scene.
            this.renderer.display();
        }

        this.popMatrix();
        // ---- END Background, camera and axis setup
    }
}

import { CGFapplication } from '../lib/CGF.js';
import { XMLscene } from './XMLscene.js';
import { MyInterface } from './MyInterface.js';
import { MySceneGraph } from './MySceneGraph.js';

function getUrlVars() {
    var vars = {};
    var parts = window.location.href.replace(/[?&]+([^=&]+)=([^&]*)/gi,    
    function(m,key,value) {
      vars[decodeURIComponent(key)] = decodeURIComponent(value);
    });
    return vars;
}	 

function main() {

	// Standard application, scene and interface setup
    var app = new CGFapplication(document.body);
    var myInterface = new MyInterface();
    var myScene = new XMLscene(myInterface);

    app.init();

    app.setScene(myScene);
    app.setInterface(myInterface);

    myInterface.setActiveCamera(myScene.camera);

	// get file name provided in URL, e.g. http://localhost/myproj/?file=myfile.xml 
	// or use "demo.xml" as default (assumes files in subfolder "scenes", check MySceneGraph constructor) 

    var filename=getUrlVars()['file'] || "demo.xml";

	// create and load graph, and associate it to scene. 
	// Check console for loading errors
	var myGraph = new MySceneGraph(filename, myScene);

	// start
    app.run();
}

main();

/**
 * Model representing a RGBA color
 */
export class Color {
    /**
     * @param {float} r - Red component
     * @param {float} g - Green component
     * @param {float} b - Blue component
     * @param {float} a - Alpha component
     */
    constructor(r, g, b, a){
        this.r = r;
        this.g = g;
        this.b = b;
        this.a = a;
    }

    /**
     * Gets the color components in a [r, g, b, a] array
     * @returns {Array<float>} - Color components
     */
    getArray() {
        return [this.r, this.g, this.b, this.a];
    }
}

/**
 * Model representing a 3D coordinate
 */
export class Coordinate3D {
    /**
     * @param {float} x - x coordinate
     * @param {float} y - y coordinate
     * @param {float} z - z coordinate
     */
    constructor(x, y, z){
        this.x = x;
        this.y = y;
        this.z = z;
    }

    /**
     * Gets the coordinate components in a [x, y, z] array
     * @returns {Array<float>} - Coordinate components
     */
    getArray() {
        return [this.x, this.y, this.z];
    }
}

/**
 * Model that represents a rotation
 */
export class Rotation {
    /**
     * @param {string} axis - Axis of the rotation
     * @param {number} angle - Angle of the rotation in degrees
     */
    constructor(axis, angle){
        this.axis = axis;
        let DEGREE_TO_RAD = Math.PI / 180;
        this.angle = angle * DEGREE_TO_RAD;
    }

    /**
     * Gets the axis array representation
     * @returns Axis array representation
     */
    getAxisArray(){
        switch (this.axis) {
            case 'x': return [1, 0, 0];
            case 'y': return [0, 1, 0];
            case 'z': return [0, 0, 1];
        }
        return [0, 0, 0];
    }

    /**
     * Gets the angle of the rotation in degrees
     * @returns Angle of the rotation in radians
     */
    getAngle() {
        return this.angle;
    }
}

/**
 * Model that contains information about a scene
 */
export class SceneData {
    /**
     * @param {CGFscene} scene - Reference to the CGFscene
     */
    constructor(scene) {
        // Global values
        this.scene = scene;
        this.root = null;
        this.referenceLength = 1;
        this.ambient = [];
        this.background = [];

        // Active (default) values
        this.defaultView = "";

        // Lists of elements
        this.views = {};
        this.lights = {};
        this.textures = {};
        this.materials = {};
        this.transformations = {};
        this.primitives = {};
        this.components = {}
    }
}

/**
 * Component Node representation, contains data from <component>
 */
 export class ComponentNode {
    /**
    * @param {string} id - Node id
    * @param {MyTransformation} transformation - Id of the transformation used
    * @param {Array<MyMaterial>} materials - List of the materials used, can be MyMaterial or "inherit"
    * @param {MyTexture|string} texture - Texture used, can be "inherit", "none" or a MyTexture object
    * @param {Array<string>} childComponentsId - Ids of all the children components
    * @param {Array<string>} childPrimitivesId - Ids of all the children primitives
    */
    constructor(id, transformation, materials, texture, childComponentsId, childPrimitivesId) {
        this.id = id;
        this.transformation = transformation;
        this.materials = materials;
        this.currentMaterial = 0;
        this.texture = texture;
        this.childComponentsId = childComponentsId;
        this.childPrimitivesId = childPrimitivesId;
    }

    /**
     * Get the id of the component
     * @returns {string} - Id of the component
     */
    getId() {
        return this.id;
    }

    /**
     * Set the component's children, components or primitives
     * @param {MyComponentNode|MyPrimitiveNode} children - Array of child nodes
     */
    setChildren(children) {
        this.children = children;
    }

    /**
     * Get list of child nodes
     * @returns {Array<MyComponentNode|MyPrimitiveNode>} - List of the children
     */
    getChildren() {
        return this.children;
    }

    /**
     * Get the transformation object
     * @returns {MyTransformation} transformation - Transformation object
     */
    getTransformation() {
        return this.transformation;
    }

    /**
     * Get the current material object
     * @returns {MyMaterial|string} - Current material object or "inherit"
     */
    getMaterial() {
        return this.materials[this.currentMaterial];
    }

    /**
     * Get the texture object
     * @returns {MyTexture|string} - Texture object or "inherit" or "none"
     */
    getTexture() {
        return this.texture;
    }

    /**
     * Update current material to the next one on the materials list
     */
    updateMaterial() {
        this.currentMaterial = (this.currentMaterial + 1) % this.materials.length;
    }
}

