import { ParserResult } from "./ParserResult.js";

/**
 * Parser for the <lights> block
 * This is an wrapper for the base code implementation of this functionality
 */
export class LightsParser {
    /**
     * Parse the <lights> block
     * @param {lights element} node - Node that should be parsed
     * @param {CGFXMLreader} reader - XMLreader
     * @returns {ParserResult} - Containing a array containing the parsed lights, and errors that occurred while parsing
     */
    static parse(node, reader) {
        const children = node.children;

        let errors = [];

        let lights = [];
        let numLights = 0;

        let grandChildren = [];
        let nodeNames = [];

        // Any number of lights.
        for (let i = 0; i < children.length; i++) {

            // Storing light information
            let global = [];
            let attributeNames = [];
            let attributeTypes = [];

            //Check type of light
            if (children[i].nodeName != "omni" && children[i].nodeName != "spot") {
                errors.push("unknown tag <" + children[i].nodeName + ">");
                continue;
            }
            else {
                attributeNames.push(...["location", "ambient", "diffuse", "specular"]);
                attributeTypes.push(...["position", "color", "color", "color"]);
            }

            // Get id of the current light.
            let lightId = reader.getString(children[i], 'id');
            if (lightId == null) {
                errors.push("no ID defined for light");
                continue;
            }

            // Checks for repeated IDs.
            if (lights[lightId] != null) {
                errors.push("ID must be unique for each light (conflict: id= " + lightId + ")");
                continue;
            }

            // Light enable/disable
            let enableLight = true;
            var aux = reader.getBoolean(children[i], 'enabled');
            if (!(aux != null && !isNaN(aux) && (aux == true || aux == false)))
                errors.push("unable to parse value component of the 'enable light' field for id= " + lightId + "; assuming 'value = 1'");

            enableLight = aux && 1;

            //Add enabled boolean and type name to light info
            global.push(enableLight);
            global.push(children[i].nodeName);

            grandChildren = children[i].children;
            // Specifications for the current light.

            nodeNames = [];
            for (let j = 0; j < grandChildren.length; j++) {
                nodeNames.push(grandChildren[j].nodeName);
            }


            let hasUndefinedAttribute = false;
            for (let j = 0; j < attributeNames.length; j++) {
                let attributeIndex = nodeNames.indexOf(attributeNames[j]);


                if (attributeIndex != -1) {
                    if (attributeTypes[j] == "position")
                        var aux = this.parseCoordinates4D(grandChildren[attributeIndex], reader, "light position for ID" + lightId);
                    else
                        var aux = this.parseColor(grandChildren[attributeIndex], reader, attributeNames[j] + " illumination for ID" + lightId);

                    if (!Array.isArray(aux)){
                        errors.push("light attribute " + attributeNames[j] + " undefined for id=" + lightId);
                        hasUndefinedAttribute = true;
                        continue;
                    }


                    global.push(aux);
                }
                else {
                    errors.push("light " + attributeNames[i] + " undefined for id=" + lightId);
                    hasUndefinedAttribute = true;
                }
            }
            if(hasUndefinedAttribute) {
                continue;
            }

            // Gets the additional attributes of the spot light
            if (children[i].nodeName == "spot") {
                var angle = reader.getFloat(children[i], 'angle');
                if (!(angle != null && !isNaN(angle))) {
                    errors.push("unable to parse angle of the light for id= " + lightId);
                    continue;
                }

                var exponent = reader.getFloat(children[i], 'exponent');
                if (!(exponent != null && !isNaN(exponent))) {
                    errors.push("unable to parse exponent of the light for id= " + lightId);
                    continue;
                }

                let targetIndex = nodeNames.indexOf("target");

                // Retrieves the light target.
                var targetLight = [];
                if (targetIndex != -1) {
                    let aux = this.parseCoordinates3D(grandChildren[targetIndex], reader, "target light for ID " + lightId);
                    if (!Array.isArray(aux)){
                        errors.push(aux);
                        continue;
                    }


                    targetLight = aux;
                }
                else {
                    errors.push("light target undefined for id= " + lightId);
                    continue;
                }

                global.push(...[angle, exponent, targetLight])
            }

            lights[lightId] = global;
            numLights++;
        }

        if (numLights == 0)
            errors.push("at least one light must be defined");
        else if (numLights > 8)
            errors.push("too many lights defined; WebGL imposes a limit of 8 lights");

        return new ParserResult(lights, errors);
    }

    /**
     * Parse the coordinates from a node with id=id
     * @param {block element} node - Node that should be parsed
     * @param {CGFXMLreader} reader - XMLreader
     * @param {message to be displayed in case of error} messageError - Message to be displayed in case of error
     * @returns {Array} - Containing the parsed coordinates
    */
    static parseCoordinates3D(node, reader, messageError) {
        var position = [];

        // x
        var x = reader.getFloat(node, 'x');
        if (!(x != null && !isNaN(x)))
            return "unable to parse x-coordinate of the " + messageError;

        // y
        var y = reader.getFloat(node, 'y');
        if (!(y != null && !isNaN(y)))
            return "unable to parse y-coordinate of the " + messageError;

        // z
        var z = reader.getFloat(node, 'z');
        if (!(z != null && !isNaN(z)))
            return "unable to parse z-coordinate of the " + messageError;

        position.push(...[x, y, z]);

        return position;
    }

    /**
     * Parse the coordinates from a node with id=id
     * @param {block element} node - Node to be parsed
     * @param {CGFXMLreader} reader - XMLreader
     * @param {message to be displayed in case of error} messageError - Message to be displayed in case of error
     * @returns {Array} - Containing the parsed coordinates
     */
    static parseCoordinates4D(node, reader, messageError) {
        var position = [];

        //Get x, y, z
        position = this.parseCoordinates3D(node, reader, messageError);

        if (!Array.isArray(position))
            return position;


        // w
        var w = reader.getFloat(node, 'w');
        if (!(w != null && !isNaN(w)))
            return "unable to parse w-coordinate of the " + messageError;

        position.push(w);

        return position;
    }

    /**
     * Parse the color components from a node
     * @param {block element} node - Node to be parsed
     * @param {CGFXMLreader} reader - XMLreader
     * @param {message to be displayed in case of error} messageError - Message to be displayed in case of error
     * @returns {Array} - Containing the parsed color
     */
    static parseColor(node, reader, messageError) {
        var color = [];

        // R
        var r = reader.getFloat(node, 'r');
        if (!(r != null && !isNaN(r) && r >= 0 && r <= 1))
            return "unable to parse R component of the " + messageError;

        // G
        var g = reader.getFloat(node, 'g');
        if (!(g != null && !isNaN(g) && g >= 0 && g <= 1))
            return "unable to parse G component of the " + messageError;

        // B
        var b = reader.getFloat(node, 'b');
        if (!(b != null && !isNaN(b) && b >= 0 && b <= 1))
            return "unable to parse B component of the " + messageError;

        // A
        var a = reader.getFloat(node, 'a');
        if (!(a != null && !isNaN(a) && a >= 0 && a <= 1))
            return "unable to parse A component of the " + messageError;

        color.push(...[r, g, b, a]);

        return color;
    }
}

import { ParserResult } from "./ParserResult.js";
import { ColorParser } from "./ColorParser.js";
import { Color } from "../models/Color.js";
import { FloatParser } from "./FloatParser.js";
import { BlockParser } from "./BlockParser.js";
import { MyMaterial } from "../models/wrappers/MyMaterial.js";

export class MaterialParser {
    static parse(node, reader, scene) {
        if (node.nodeName != "material") {
            return ParserResult.fromError("unknown tag <" + node.nodeName + ">");
        }

        let id = reader.getString(node, "id");
        if (id == null) {
            return ParserResult.fromError("no ID defined for material");
        }

        let shininessResult = FloatParser.parse(node, reader, "shininess");

        const defaultColor = new Color(0,0,0,1);

        const func = (n) => ColorParser.parse(n, reader);
        const handlerEntries = {
            "emission": [func, defaultColor],
            "ambient": [func, defaultColor],
            "diffuse": [func, defaultColor],
            "specular": [func, defaultColor],
        };
        const handlerMap = new Map(Object.entries(handlerEntries));
        const parseResult = BlockParser.parse(node, handlerMap);

        const components = parseResult.getValue();
        let mat = MyMaterial.instantiate(
            id,
            components["ambient"],
            components["diffuse"],
            components["specular"],
            components["emission"],
            shininessResult.getValueOrDefault(10),
            scene
        );
        return ParserResult.collect(mat, [shininessResult, parseResult], "parsing <material> with id=" + id);
    }
}

export class ParserErrorPrinter {
    static print(error, nesting_level=0) {
        if(Array.isArray(error)){
            for(const e of error){
                if (typeof e === 'string' || e instanceof String){
                    console.warn(" ".repeat(nesting_level*4) + e);
                } else {
                    for(const [context, otherErrors] of Object.entries(e)) {
                        console.warn(" ".repeat(nesting_level*4) + "context: " + context);
                        this.print(otherErrors, nesting_level+1);
                    }
                }
            }
        } else {
            console.log(" ".repeat(nesting_level*4) + error);
        }
    }
}

export class ParserResult {
    constructor(value, errors){
        this.value = value;
        this.errors = errors;
    }

    static fromError(error) {
        return new ParserResult(null, [error]);
    }

    static fromValue(value) {
        return new ParserResult(value, []);
    }

    static collect(value, parserResults, context, other_errors=[]){
        let errors = other_errors;
        for(const parserResult of parserResults){
            errors = errors.concat(parserResult.getErrors());
        }
        if(errors.length > 0){
            let k = {};
            k[context] = errors;
            return new ParserResult(value, [k]);
        }
        return this.fromValue(value);
    }

    getValue() {
        return this.value;
    }

    getValueOrDefault(defaultValue) {
        return this.value == null ? defaultValue : this.value;
    }

    getErrors() {
        return this.errors;
    }

    hasError() {
        return this.errors.length > 0;
    }
}

import { ParserResult } from "./ParserResult.js";
import { FloatParser } from "./FloatParser.js";
import { IntegerParser } from "./IntegerParser.js";
import { MyRectangle } from "../primitives/MyRectangle.js";
import { MyTriangle } from "../primitives/MyTriangle.js";
import { MyCylinder } from "../primitives/MyCylinder.js";
import { MySphere } from "../primitives/MySphere.js";
import { MyTorus } from "../primitives/MyTorus.js";
import {PrimitiveNode} from "../models/graph/PrimitiveNode.js";

export class PrimitiveParser {
    static parse(node, reader, scene) {
        if (node.nodeName !== "primitive") {
            return ParserResult.fromError("unknown tag <" + node.nodeName + ">");
        }

        let id = reader.getString(node, "id");
        if (id == null) {
            return ParserResult.fromError("no id defined for primitive");
        }

        if (node.children.length === 1) {
            let childNode = node.children[0];
            let primitiveType = childNode.nodeName;

            if (primitiveType === 'rectangle') {
                return PrimitiveParser.parseRectangle(childNode, reader, scene, id);
            } else if (primitiveType === 'triangle') {
                return PrimitiveParser.parseTriangle(childNode, reader, scene, id);
            } else if (primitiveType === 'cylinder') {
                return PrimitiveParser.parseCylinder(childNode, reader, scene, id);
            } else if (primitiveType === 'sphere') {
                return PrimitiveParser.parseSphere(childNode, reader, scene, id);
            } else if (primitiveType === 'torus') {
                return PrimitiveParser.parseTorus(childNode, reader, scene, id);
            }
        }
        return ParserResult.fromError("There must be exactly 1 primitive type (rectangle, triangle, cylinder, sphere or torus)");
    }

    static parseRectangle(node, reader, scene, id) {
        let x1 = FloatParser.parse(node, reader, 'x1');
        let y1 = FloatParser.parse(node, reader, 'y1');
        let x2 = FloatParser.parse(node, reader, 'x2', x1.getValueOrDefault(0));
        let y2 = FloatParser.parse(node, reader, 'y2', y1.getValueOrDefault(0));

        let rectangle = new MyRectangle(
            scene, 
            x1.getValueOrDefault(0), 
            x2.getValueOrDefault(1), 
            y1.getValueOrDefault(0), 
            y2.getValueOrDefault(1)
        );
        return ParserResult.collect(
            new PrimitiveNode(id, rectangle),
            [x1, y1, x2, y2],
            "parsing <rectangle> with id=" + id
        );
    }

    static parseTriangle(node, reader, scene, id) {
        let x1 = FloatParser.parse(node, reader, 'x1');
        let y1 = FloatParser.parse(node, reader, 'y1');
        let z1 = FloatParser.parse(node, reader, 'z1');

        let x2 = FloatParser.parse(node, reader, 'x2');
        let y2 = FloatParser.parse(node, reader, 'y2');
        let z2 = FloatParser.parse(node, reader, 'z2');

        let x3 = FloatParser.parse(node, reader, 'x3');
        let y3 = FloatParser.parse(node, reader, 'y3');
        let z3 = FloatParser.parse(node, reader, 'z3');

        let triangle = new MyTriangle(
            scene, 
            x1.getValueOrDefault(0), 
            x2.getValueOrDefault(1),
            x3.getValueOrDefault(0),
            y1.getValueOrDefault(0),
            y2.getValueOrDefault(0),
            y3.getValueOrDefault(1),
            z1.getValueOrDefault(0),
            z2.getValueOrDefault(0),
            z3.getValueOrDefault(0)
        );

        return ParserResult.fromValue(
            new PrimitiveNode(id, triangle),
            [x1,y1,z1,x2,y2,z2,x3,y3,z3],
            "parsing <triangle> with id=" + id);
    }

    static parseCylinder(node, reader, scene, id) {
        let baseRadius = FloatParser.parse(node, reader, 'base', 0);
        let topRadius = FloatParser.parse(node, reader, 'top', 0);
        let height = FloatParser.parse(node, reader, 'height', 0);
        let slices = IntegerParser.parse(node, reader, 'slices', 1);
        let stacks = IntegerParser.parse(node, reader, 'stacks', 1);

        let collection = ParserResult.collect(null, [baseRadius, topRadius, height, slices, stacks]);
        if (collection.hasError()) {
            return collection;
        }

        let cylinder = new MyCylinder(
            scene, 
            baseRadius.getValueOrDefault(1),
            topRadius.getValueOrDefault(1),
            height.getValueOrDefault(1),
            slices.getValueOrDefault(8),
            stacks.getValueOrDefault(8)
        );

        return ParserResult.collect(
            new PrimitiveNode(id, cylinder),
            [baseRadius, topRadius, height, slices, stacks],
            "parsing <cylinder> with id=" + id
        );
    }

    static parseSphere(node, reader, scene, id) {
        let radius = FloatParser.parse(node, reader, 'radius', 0);
        let slices = IntegerParser.parse(node, reader, 'slices', 1);
        let stacks = IntegerParser.parse(node, reader, 'stacks', 1);

        let sphere = new MySphere(
            scene, 
            radius.getValueOrDefault(1),
            slices.getValueOrDefault(8),
            stacks.getValueOrDefault(8)
        );

        return ParserResult.collect(
            new PrimitiveNode(id, sphere),
            [radius, slices, stacks],
            "parsing <sphere> with id=" + id
        );
    }

    static parseTorus(node, reader, scene, id) {
        let inner = FloatParser.parse(node, reader, 'inner', 0);
        let outer = FloatParser.parse(node, reader, 'outer', 0);
        let slices = IntegerParser.parse(node, reader, 'slices', 1);
        let loops = IntegerParser.parse(node, reader, 'loops', 1);

        let torus = new MyTorus(
            scene, 
            inner.getValueOrDefault(1),
            outer.getValueOrDefault(1),
            slices.getValueOrDefault(8),
            loops.getValueOrDefault(8)
        );

        return ParserResult.collect(
            new PrimitiveNode(id, torus),
            [inner, outer, slices, loops],
            "parsing <torus> with id=" + id
        );
    }
}

import { ParserResult } from "./ParserResult.js";
import { Rotation } from "../models/Rotation.js";
import { FloatParser } from "./FloatParser.js";

export class RotationParser {
    static parse(node, reader){
        let errors = [];

        // Get axis
        const axis = reader.getString(node, 'axis');
        if (!(axis != null && axis.length === 1 && ['x', 'y', 'z'].includes(axis)))
            return ParserResult.fromError("unable to parse axis");

        // Get angle
        const angleResult = FloatParser.parse(node, reader, 'angle');
        if(angleResult.hasError()){
            return new ParserResult(null, angleResult.getErrors());
        }

        return ParserResult.fromValue(new Rotation(axis, angleResult.getValue()));
    }
}

